head.script:
//
			function confirm() {
				var input = document.getElementById("password-input")
				var confirm = document.getElementById("confirm-password-input")
				if (input.value == confirm.value) {
					confirm.style.outlineWidth = "0px"
				} else {
					confirm.style.outlineWidth = "2px"
					confirm.style.outlineColor = "red"
					confirm.style.outlineStyle = "ridge"
				}
			}
			function savePassword(value) {
				var name = document.getElementById("name-input").value
				var realm = document.getElementById("realm-span")
				realm = realm.getAttribute("resource") || realm.textContent || realm.innerText
				var encoded = hex_md5(name + ':' + realm + ':' + value)
				var input = document.getElementById("algorithm-input")
				input.setAttribute("property", "calli:algorithm")
				input.setAttribute("content", "MD5")
				input = document.getElementById("encoded-input")
				input.setAttribute("property", "calli:encoded")
				input.setAttribute("content", encoded)
				input.setAttribute("datatype", "xsd:hexBinary")
			}
			//
